@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

#page {
  font-family: system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

header {
  color: var(--color-white, white);
  position: relative;
  overflow: hidden;
  background: var(--color-background);

  &.simple {
    background: var(--color-background);
  }
}

img {
  position: absolute;
  inset: 0;
  height: 100%;
  width: 100%;
  object-fit: cover;
  z-index: 0;
}

.heading-group {
  background: #12121260; /* overlay */
  display: grid;
  place-items: center;
  padding-top: 12rem;
  padding-bottom: 12rem;
  position: relative;
  gap: 1rem;

  .heading {
    font-size: clamp(2rem, 10vw, 3.5rem);
    color: var(--color-white, white);
  }

  .subheading {
    place-self: center;
    font-size: 1.125rem;
    line-height: 1.3;
    color: var(--color-inverted);
  }
}

.buttons {
  margin-top: 1rem;
  display: flex;
  justify-content: center;
  gap: 1rem;

  .button {
    background: transparent;
    color: var(--color-inverted);
    border: 1.5px solid white;
  }
}

svg {
  width: 100%;
  background: #183b56a6;
  position: relative;
  fill: var(--background);
}

section {
  background: var(--color-brand);
  overflow: hidden;

  &.image-left {
    figure {
      grid-column: 1;
      grid-row: 1;
    }
    .body {
      grid-column: 2;
    }
  }
}

.section-container {
  display: flex;
  flex-direction: column-reverse;
  gap: 2rem;

  @media (min-width: 800px) {
    display: grid;
    grid-template-columns: 1fr 1fr;
    place-items: center;
    gap: 6rem;
    padding-block: 6rem;
  }
}

.body {
  color: var(--color-inverted);
  display: grid;
  place-items: start;
  padding-top: 2rem;

  .headline {
    color: var(--color-heading);
    font-family: var(--font-heading);
    font-weight: 700;
    font-size: clamp(2.75rem, 10vw, 3rem);
    line-height: 1.1;
    margin-bottom: 1.5rem;
  }

  .subheading {
    font-weight: 400;
    font-size: 1.125rem;
    margin-bottom: 2rem;
  }

  .button {
    font-weight: 500;
  }
}

figure {
  position: relative;

  img {
    border-radius: var(--border-radius);
    z-index: 1;
    position: relative;
  }
}

