@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

#page {
  font-family: system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

.content {
  :global(img) {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  :global(p) {
    padding: 0.5rem 0;
    line-height: 1.5;
  }

  :global(a) {
    text-decoration: underline;
  }

  :global(h1) {
    font-size: 2.5rem;
    font-weight: 600;
    line-height: 1.2;
    margin-bottom: 0.5rem;
  }

  :global(h2) {
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
    line-height: 1.2;
  }

  :global(h3) {
    font-size: 1.5rem;
    font-weight: 600;
    line-height: 1.2;
    margin-top: .5rem;
  }

  :global(ul) {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  :global(ol) {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  :global(blockquote) {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.section-container { /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  padding-top: 1rem;
  padding-bottom: 1rem;

  @media (min-width: 600px) {
    flex-direction: row;
  }
}

nav {
  display: flex;
  gap: 1rem;
  font-size:.875rem;

  .link { /* Defined in Site CSS */
    border-color: transparent;

    &:hover {
      border-color: initial;
    }
  }
}

.primo {
  font-weight: 600;
  font-size: 0.75rem;

  a {
    text-decoration: underline; 
  }
}

.social-links {
  display: flex;
  gap: 1.5rem;
  font-size: 1.5rem;

  a {
    color: var(--color-accent, rebeccapurple);
    transition: var(--transition, 0.1s transform);

    &:hover {
      transform: scale(1.1);
    }
  }
}

header {
  color: var(--color-white, white);
  position: relative;
  overflow: hidden;
  background: var(--color-background);

  &.simple {
    background: var(--color-background);
  }
}

img {
  position: absolute;
  inset: 0;
  height: 100%;
  width: 100%;
  object-fit: cover;
  z-index: 0;
}

.heading-group {
  background: #12121260; /* overlay */
  display: grid;
  place-items: center;
  padding-top: 12rem;
  padding-bottom: 12rem;
  position: relative;
  gap: 1rem;

  .heading {
    font-size: clamp(2rem, 10vw, 3.5rem);
    color: var(--color-white, white);
  }

  .subheading {
    place-self: center;
    font-size: 1.125rem;
    line-height: 1.3;
    color: var(--color-inverted);
  }
}

.buttons {
  margin-top: 1rem;
  display: flex;
  justify-content: center;
  gap: 1rem;

  .button {
    background: transparent;
    color: var(--color-inverted);
    border: 1.5px solid white;
  }
}

svg {
  width: 100%;
  background: #183b56a6;
  position: relative;
  fill: var(--background);
}

