  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

iframe{
  width:100vw;
  height: 100vh;

  margin:auto;
  margin-top: 10px;
  max-height:none;
}

header {
  color: var(--color-white, white);
  position: relative;
  overflow: hidden;
  background: var(--color-background);
  background: url(https://media3.giphy.com/avatars/kendallaking/qYZoVGDtfIKh.GIF);
  &.simple {
    background: var(--color-background);
  }
}

img {
  position: absolute;
  inset: 0;
  left: 25%;
  right: 25%;
  height: 50%;
  width: 50%;
  /* object-fit: cover; */
  z-index: 0;
}

.heading-group {
  background: #12121260; /* overlay */
  display: grid;
  place-items: center;
  padding-top: 12rem;
  padding-bottom: 12rem;
  position: relative;
  gap: 1rem;

  .heading {
    font-size: clamp(2rem, 10vw, 3.5rem);
    color: var(--color-white, white);
  }

  .subheading {
    place-self: center;
    font-size: 1.125rem;
    line-height: 1.3;
    color: var(--color-inverted);
  }
}

.buttons {
  margin-top: 1rem;
  display: flex;
  justify-content: center;
  gap: 1rem;

  .button {
    background: transparent;
    color: var(--color-inverted);
    border: 1.5px solid white;
  }
}

svg {
  width: 100%;
  background: #183b56a6;
  position: relative;
  fill: var(--background);
